mapper.select:
select

from patrolrecord
where sys_patrol_record_id = #{sysPatrolRecordId,jdbcType=INTEGER}

SELECT DISTINCT pr.sys_patrol_record_id,pr.*,s.*,sf.*
FROM section s,patrolrecord pr,staff sf,role
WHERE sf.sys_section_id=s.sys_section_id AND pr.sys_staff_id=sf.sys_staff_id AND role.sys_role_id=sf.sys_role_id

    AND sf.sys_section_id = #{sysSectionId}


    AND s.section_pid = #{sectionPid}


    AND pr.patrol_record_begintime> #{beginTime}


    AND pr.patrol_record_endtime < #{endTime}

order by patrol_record_begintime DESC

SELECT * FROM patrolrecord p,staff s,road r WHERE p.sys_staff_id=s.sys_staff_id
and p.roadName like CONCAT('%',#{roadName},'%')
AND patrol_record_begintime >= #{beginTime}
AND patrol_record_begintime <= #{endTime}
ORDER BY patrol_record_begintime DESC

SELECT DISTINCT pr.sys_patrol_record_id,pr.*,s.*,sf.*
FROM section s,patrolrecord pr,staff sf,role
WHERE sf.sys_section_id=s.sys_section_id AND pr.sys_staff_id=sf.sys_staff_id AND role.sys_role_id=sf.sys_role_id

    AND sf.sys_staff_id=#{staffid}


    AND pr.patrol_record_begintime> #{beginTime}


    AND pr.patrol_record_endtime < #{endTime}

order by patrol_record_begintime DESC

SELECT *
FROM section s,patrolrecord pr,staff sf

    sf.sys_section_id=s.sys_section_id AND pr.sys_staff_id=sf.sys_staff_id
    AND sf.sys_staff_id=#{SysStaffId}

        AND patrol_record_begintime >= #{beginTime}

    AND patrol_record_begintime <=
        #{endTime}


order by patrol_record_begintime DESC

SELECT section_name,COUNT(*) FROM patrolrecord AS p,staff AS s,section WHERE p.`sys_staff_id` =s.`sys_staff_id` AND s.`sys_section_id` =section.`sys_section_id`
AND `patrol_record_begintime` BETWEEN #{startDate} AND #{endDate}
GROUP BY section.section_name

mapper.delete:
delete from patrolrecord
where sys_patrol_record_id in
(

    #{sysPatrolRecordId}

)

mapper.select:
select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now()) and x.station = #{station}
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment = '3' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment = '2' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and (x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%' and conment != '2' and conment != '3' and station = '区域' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now()) and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment != '3'
and x.battalion = #{battalion}
and ((((x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%') or x.station = '高速岗' or conment = '2' or x.station = '固定岗' or conment = '4') or (station != '固定岗' and station != '高峰岗' and station != '高速岗' and conment != '2' and conment != '4' and station != '快速岗' and station != '区域')) and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment = '4' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now()) and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id
and x.battalion = #{battalion}
and x.station != '固定岗' and x.station != '高峰岗' and x.station != '高速岗' and x.conment != '2' and x.conment != '4' and x.station != '快速岗' and x.station != '区域' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl
where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and x.detachment = #{detachment}
and  p.sys_staff_id = s.sys_staff_id
and s.sys_staff_id = xl.staff_id
and xl.xarea_id = x.id
and s.stafftype=#{stafftype}
and ((((x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%') or x.station = '高速岗' or x.conment = '2' or x.station = '固定岗' or x.conment = '4'

    or x.station = #{station}
)
or (station != '固定岗' and station != '高峰岗' and station != '高速岗' and conment != '2' and conment != '4' and station != '快速岗' and station != '区域'))
and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl
where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and x.detachment = #{detachment}
and s.stafftype=#{stafftype}
and  p.sys_staff_id = s.sys_staff_id
and s.sys_staff_id = xl.staff_id
and xl.xarea_id = x.id
and x.conment = '3'
and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now())
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment = '3' and (x.detachment like '%夜巡%' or x.gridding like '%夜巡%') and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(now()) and x.station = #{station}
and x.battalion = #{battalion}
and x.conment != 3
and x.conment = #{conment}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(#{time})
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment = '3' and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(#{time}) and x.station = #{station}
and x.battalion = #{battalion}
and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select p.sys_staff_id from patrolrecord p,staff s,xarea x,xrelation xl where to_days(p.patrol_record_begintime) = to_days(#{time}) and p.sys_staff_id = s.sys_staff_id and s.sys_staff_id = xl.staff_id and xl.xarea_id = x.id and x.conment != '3'
and x.battalion = #{battalion}
and ((((x.gridding like '%网格%' or x.type = '面') and x.gridding is not null and x.gridding not like '%级网格%') or x.station = '高速岗' or conment = '2' or x.station = '固定岗' or conment = '4') or (station != '固定岗' and station != '高峰岗' and station != '高速岗' and conment != '2' and conment != '4' and station != '快速岗' and station != '区域')) and p.patrol_record_endtime is null GROUP BY p.sys_staff_id

select st.section_name as name,st.sys_section_id as id from xarea x,section st where x.battalion = st.section_name
and st.section_name = #{sectionName}
GROUP BY st.section_name
